drop database friend_db;

CREATE DATABASE friend_db;

USE friend_db;


CREATE TABLE friends(
id INTEGER(11) NOT NULL AUTO_INCREMENT,
name VARCHAR(25),
friend_desc VARCHAR(150),
pic_url TEXT(500),
answers VARCHAR(50),
PRIMARY KEY (id)
);

